import {
  Banner,
  BestGoods,
  Contacts,
  ProductCategories,
  ProductList,
  Sale,
} from "../../components";
import Wrapper from "../../components/Wrapper/Wrapper";
import { useAppDispatch, useAppSelector } from "../../store";
import { markAsPopular, selectPopularProducts } from "../../store/slices/productsSlice";
import styles from "./styles.module.scss";

export const MainPage = () => {
  const dispatch = useAppDispatch();
  const popularProducts = useAppSelector(selectPopularProducts);

  const handleMarkAsPopular = (productId: string) => {
    dispatch(markAsPopular(productId));
  };

  return (
    <main className={styles.main}>
      <Banner isBreadcrumbHidden={true} />
      <Wrapper>
        <h1 className={styles.title}>
          <span style={{ color: "#FFC85E", fontWeight: "bold" }}>
            Акционные
          </span>
          &nbsp;
          <span style={{ color: "#111111" }}>товары</span>
        </h1>
        <ProductList
          products={popularProducts.slice(0, 8)}
          onMarkAsPopular={handleMarkAsPopular}
          className={styles.list}
        />
        <ProductCategories />
        <Sale />
        <BestGoods />
        <Contacts />
      </Wrapper>
    </main>
  );
};
