import { createSlice } from '@reduxjs/toolkit';
import { Product } from '../../services/products';

type State = {
  allProducts: Product[];
  popularProducts: Product[];
};

const initialState: State = {
  allProducts: [],
  popularProducts: [],
};

const productsSlice = createSlice({
  name: 'products',
  initialState,
  reducers: {
    filterByPopularity: (state) => {
      state.popularProducts = state.allProducts.filter((product) => product.popular);
    },
  },
});

export const { filterByPopularity } = productsSlice.actions;

export default productsSlice.reducer;
